$(function(){$(".video-vote a").click(function(e){var t=$("#user-info").val();if(t){var o=$(this).attr("href"),i=$(this).attr("data-method");$.ajax({url:o,method:i,success:function(e){$(".video-vote .like-vote-"+e.id).html(e.upvotes),$(".video-vote .unlike-vote-"+e.id).html(e.downvotes)},error:function(){console.log("ajax error")}})}else alert("Please login first to vote the video");return e.preventDefault(),!1})});